const ctx = document.getElementById('canvas');
const myChart = new Chart(ctx, {
    type: 'bar',
    data: {
        labels: [
            "19/5",
            "18/4",
            "17/6",
            "20/7",
            "22/7",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8",
            "30/8"
        ],
        datasets: [{
                label: "Adam",
                backgroundColor: "#E5F0F7",
                borderColor: "#0575B6",
                borderWidth: 1,

                data: [60, 63, 57, 59, 56, 46, 56, 50, 65, 55, 45, 60, 49, 57, 62, 30, 50, 40]
            },
            {
                label: "Average",
                backgroundColor: "#E5E5E5",
                borderColor: "#333333",
                borderWidth: 1,

                data: [57, 65, 60, 62, 58, 51, 55, 45, 60, 57, 49, 58, 52, 54, 55, 50, 60, 50]
            }
        ]
    },
    options: {
        responsive: true,
        borderRadius: 12,

        plugins: {

            legend: {
                position: "top",
                align: "start",

                labels: {
                    boxHeight: 15,
                    boxWidth: 5,
                    borderCapStyle: "round"

                }

            }
        },
        scales: {

            x: {
                grid: {
                    lineWidth: 0,

                }
            },
            y: {

                grid: {
                    borderDash: [8, 8],
                    borderWidth: 0,
                    borderColor: "#D7D7D7"


                },
                ticks: {
                    // Include a dollar sign in the ticks
                    callback: function(value, index, values) {
                        value = value + 5;
                        return '%' + value;
                    }
                }
            }
        }
    }
});

